import React from 'react';
import { View, Text, Image, TouchableOpacity, StyleSheet } from 'react-native';

export default function CharacterCard({ item, onPress }) {
  return (
    <TouchableOpacity onPress={onPress} style={styles.card}>
      <Image source={{ uri: item.image_url }} style={styles.image} />
      <View style={styles.content}>
        <Text style={styles.title}>{item.name}</Text>
        {!!item.mythology && <Text style={styles.meta}>{item.mythology}</Text>}
        {!!item.short_description && (
          <Text numberOfLines={3} style={styles.desc}>{item.short_description}</Text>
        )}
      </View>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  card: {
    flexDirection: 'row',
    backgroundColor: '#1e1e1e',
    borderRadius: 8,
    padding: 10,
    marginVertical: 8,
    alignItems: 'flex-start',
  },
  image: {
    width: 90,
    height: 130,
    borderRadius: 6,
    marginRight: 10,
    backgroundColor: '#333',
  },
  content: { flex: 1 },
  title: { color: '#fff', fontSize: 16, fontWeight: '600' },
  meta: { color: '#bbb', fontSize: 13, marginTop: 2 },
  desc: { color: '#ddd', fontSize: 13, marginTop: 6 },
});
