import React, { useEffect, useMemo, useState } from 'react';
import { View, Text, FlatList, ActivityIndicator, StyleSheet, ImageBackground } from 'react-native';
import { fetchCharactersPage } from '../supabase';
import usePagination from '../utils/usePagination';
import PaginationControls from '../components/PaginationControls';
import CharacterCard from '../components/CharacterCard';
import { getBackgroundForMythology } from '../utils/backgrounds';
import SearchBar from '../components/SearchBar';

export default function CharactersScreen({ navigation }) {
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(false);
  const [bgUrl, setBgUrl] = useState(null);

  const { page, pageSize, setTotal, setPage } = usePagination({ initialPageSize: 10 });

  useEffect(() => {
    // Default background; can be adjusted based on selected mythology filter
    getBackgroundForMythology('Greek').then(setBgUrl).catch(() => setBgUrl(null));
  }, []);

  const load = async () => {
    setLoading(true);
    try {
      const { data, total } = await fetchCharactersPage(page, pageSize, { search });
      setTotal(total || 0);
      setItems(data || []);
    } catch (e) {
      setItems([]);
    } finally {
      setLoading(false);
    }
  };

  const [items, setItems] = useState([]);
  useEffect(() => { load(); }, [page, pageSize, search]);

  const renderItem = ({ item }) => (
    <CharacterCard
      item={item}
      onPress={() => navigation.navigate('MythDetail', { myth: item })}
    />
  );

  const content = (
    <View style={styles.container}>
      <SearchBar value={search} onChangeText={setSearch} placeholder="Search characters..." />
      {loading && (
        <View style={styles.loading}><ActivityIndicator color="#fff" /></View>
      )}
      {!loading && (
        <FlatList
          data={items}
          keyExtractor={(it) => String(it.id)}
          renderItem={renderItem}
          contentContainerStyle={{ paddingBottom: 20 }}
        />
      )}
      <PaginationControls
        page={page}
        pageSize={pageSize}
        totalCount={useMemo(() => items.length, [items])}
        onPageChange={setPage}
      />
    </View>
  );

  return bgUrl ? (
    <ImageBackground source={{ uri: bgUrl }} style={{ flex: 1 }} resizeMode="cover">
      {content}
    </ImageBackground>
  ) : content;
}

const styles = StyleSheet.create({
  container: { flex: 1, padding: 12 },
  loading: { paddingVertical: 16 },
});
